mapper.select:
		select `coach_id`,`coach_name`,`tel`,`nick_name`,`type`,group_concat(`club_name`) as `club_name`,`status`,`registration_time` from v_coach_club

		  		   and coach_id = #{coachId} 
		  		   and coach_name = #{coachName} 
		  		   and tel = #{tel} 
		  		   and nick_name = #{nickName} 
		  		   and type = #{type} 
		  		   and club_name = #{clubName} 
		  		   and status = #{status} 
		  		   and registration_time = #{registrationTime} 

		group by `coach_id`


                order by ${sort} ${order}


                order by coach_id desc



			limit #{offset}, #{limit}

select count(*) from v_coach_club

  		   and coach_id = #{coachId} 
  		   and coach_name = #{coachName} 
  		   and tel = #{tel} 
  		   and nick_name = #{nickName} 
  		   and type = #{type} 
  		   and club_name = #{clubName} 
  		   and status = #{status} 
  		   and registration_time = #{registrationTime}
